import API_ENDPOINT from"../../api/api-endpoint";class RestaurantSource{static async listRestaurants(){try{const t=await fetch(API_ENDPOINT.LIST);return(await t.json()).restaurants}catch(t){return console.error("Error fetching restaurant list:",t),null}}static async detailRestaurant(t){try{const r=await fetch(API_ENDPOINT.DETAIL(t));return(await r.json()).restaurant}catch(t){return console.error("Error fetching restaurant detail:",t),null}}static async addReview(t){try{const r=await fetch(API_ENDPOINT.REVIEW,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(t)});return(await r.json()).customerReviews}catch(t){return console.error("Error adding review:",t),null}}}export default RestaurantSource;